ALTER TABLE
crm_order_dto${uid} DROP PRIMARY KEY;
ALTER TABLE crm_order_dto${uid}
ADD PRIMARY KEY(ID);

UPDATE CRM_MEMBER_INFO_DTO${uid}
SET FIRST_PAY_TIME = DATE_ADD(FIRST_TRADE_TIME,INTERVAL 1 HOUR)
WHERE FIRST_PAY_TIME IS NULL AND TRADE_NUM > 0;

UPDATE CRM_MEMBER_INFO_DTO${uid}
SET LAST_PAY_TIME = DATE_ADD(LAST_TRADE_TIME,INTERVAL 1 HOUR)
WHERE LAST_PAY_TIME IS NULL AND TRADE_NUM > 0;

UPDATE CRM_MEMBER_INFO_DTO${uid}
SET FIRST_TRADE_FINISH_TIME = DATE_ADD(FIRST_PAY_TIME,INTERVAL 14 DAY)
WHERE FIRST_TRADE_FINISH_TIME IS NULL AND TRADE_NUM > 0;

UPDATE CRM_MEMBER_INFO_DTO${uid}
SET LAST_TRADE_FINISH_TIME = DATE_ADD(LAST_PAY_TIME,INTERVAL 14 DAY)
WHERE LAST_TRADE_FINISH_TIME IS NULL AND TRADE_NUM > 0;

alter table
crm_order_dto${uid} add column result varchar(255) comment
"订单评价结果";
alter table crm_order_dto${uid} add key index_result (result);

mapper.select:
SELECT id, sum(num) AS 'num', buyer_nick FROM crm_order_dto${uid} WHERE buyer_nick IN
#{buyerNick}
GROUP BY buyer_nick

SELECT T.BUYER_NICK, SUM(T.PAYMENT) AS 'TRADE_AMOUNT', SUM(T.NUM) AS 'ITEM_NUM' FROM CRM_TRADE_DTO${uid} T WHERE T.`STATUS` = 'TRADE_FINISHED' AND T.BUYER_NICK IN
#{buyerNick}
GROUP BY T.BUYER_NICK

SELECT T.BUYER_NICK, SUM(T.NUM) AS 'ADD_ITEM_NUM' FROM CRM_ORDER_DTO${uid} T WHERE T.BUYER_NICK IN
#{buyerNick}
GROUP BY T.BUYER_NICK

SELECT T.BUYER_NICK, SUM(T.PAYMENT) AS 'CLOSE_TRADE_AMOUNT', SUM(T.NUM) AS 'CLOSE_ITEM_NUM' FROM CRM_TRADE_DTO${uid} T WHERE T.`STATUS` IN ( 'TRADE_CLOSED', 'TRADE_CLOSED_BY_TAOBAO' ) AND T.BUYER_NICK IN
#{buyerNick}
GROUP BY T.BUYER_NICK

SELECT O.BUYER_NICK, MAX(O.TRADE_CREATED) AS 'LAST_TRADE_TIME', MIN(O.TRADE_PAY_TIME) AS 'FIRST_PAY_TIME', MIN(O.TRADE_CREATED) AS 'FIRST_TRADE_TIME', MAX(O.TRADE_PAY_TIME) AS 'LAST_PAY_TIME' FROM CRM_ORDER_DTO${uid} O WHERE O.BUYER_NICK IN
#{buyerNick}
GROUP BY O.BUYER_NICK

SELECT O.BUYER_NICK, MIN(O.TRADE_END_TIME) AS 'FIRST_TRADE_FINISH_TIME', MAX(O.TRADE_END_TIME) AS 'LAST_TRADE_FINISH_TIME' FROM CRM_ORDER_DTO${uid} O WHERE O.`STATUS` = 'TRADE_FINISHED' AND O.BUYER_NICK IN
#{buyerNick}
GROUP BY O.BUYER_NICK

SELECT O.ID, O.REFUND_STATUS AS 'TBREFUND_STATUS', O.`STATUS` AS 'TBORDER_STATUS' FROM CRM_ORDER_DTO${uid} O WHERE O.BUYER_NICK IN
#{buyerNick}
GROUP BY O.BUYER_NICK;

mapper.update:
DROP INDEX
index_buyer_nick ON crm_order_dto${uid};
DROP INDEX index_title ON
crm_order_dto${uid};
alter table crm_order_dto${uid} modify column
receiver_name text;
alter table crm_order_dto${uid} modify column
receiver_address text;
alter table crm_order_dto${uid} modify column
buyer_nick varchar(1500);
alter table crm_order_dto${uid} modify column
title text;

alter table crm_order_dto${uid} add index
index_buyer_nick(buyer_nick(255));
alter table crm_order_dto${uid} add
index index_title(title(255));
